log.info("Verifying Version API Response")

	assert jsonResponse.containsKey("timeStampUTC"), "timeStampUTC not found in response"
	assert jsonResponse.containsKey("name"), "name not found in response"
	assert jsonResponse.containsKey("type"), "type not found in response"
	assert jsonResponse.containsKey("serviceName"), "serviceName not found in response"
	assert jsonResponse.containsKey("serviceProvider"), "serviceProvider not found in response"
	assert jsonResponse.containsKey("serviceVersion"), "serviceVersion not found in response"
	assert jsonResponse.containsKey("buildNumber"), "buildNumber not found in response"

