import groovy.json.JsonSlurper

def response = context.expand( '${Step1_POSTMailboxSearch_Request#Response}' ) 
def jsonResponse = new JsonSlurper().parseText(response)

assert(jsonResponse.size()== 0 || jsonResponse.size() > 0)
log.info("Verifying Mailbox Search API Response")
if(jsonResponse.size()!= 0)

{
assert jsonResponse.size() > 0, "Response is empty"
if(jsonResponse.size() > 0)
{
for (int i = 0; i < jsonResponse.size(); i++) 
{

	def jsonObj = jsonResponse[i]
	assert jsonObj.containsKey("name"), "name not found in response at"+i
	assert jsonObj.containsKey("type"), "type not found in response at"+i
	assert jsonObj.containsKey("version"), "version not found in response at"+i
	assert jsonObj.containsKey("timestampUTC"), "timestampUTC not found in response at"+i
	assert jsonObj.containsKey("path"), "path not found in response at"+i
     assert jsonObj.containsKey("message"), "message not found in response at"+i


}
}

}
else
{
	log.info("JSON response is empty array")
}
log.info("Verified Mailbox Search API Response")

