import groovy.json.JsonSlurper 
import java.text.SimpleDateFormat
import org.joda.time.format.ISODateTimeFormat
def response = context.expand( '${Step1_POSTMailboxSearch_Request#Response}' ).toString()
def slurper = new JsonSlurper()
def jsonResponse = slurper.parseText response
log.info("Verifying timestampUTC field in JSON response")

assert(jsonResponse.size()== 0 || jsonResponse.size() > 0)

if(jsonResponse.size()!= 0)

{
assert jsonResponse.size() > 0, "Response is empty"
if(jsonResponse.size() > 0)
{
for (int i = 0; i < jsonResponse.size(); i++) {

	def jsonObj = jsonResponse[i]


def timestampUTC = jsonObj.timestampUTC
assert jsonObj.containsKey('timestampUTC'), "timestampUTC field not found"
assert timestampUTC.trim() != "", "timestampUTC field is empty"

	//def createTimeUTC = jsonResponse[i].createTimeUTC

	def dateString = timestampUTC
def dateTimeFormatStr = "yyyy-MM-dd'T'HH:mm:ss.SSS'Z'"
 assert(ISODateTimeFormat.dateTimeParser().parseDateTime(dateString)),"timestampUTC is not in format: " + dateTimeFormatStr


}
}
}
else
{
	log.info("JSON response is empty array")
}
log.info("Verified timestampUTC  field in JSON response")

