import groovy.json.JsonSlurper

//def response = context.expand( '${Step1_GetProcessorPerformance_Request#Response}' ) 
def response = context.expand( '${#[ProcessorAPI Response Format Validation TestSuite#C816729 - Validate REST endpoint#GetProcessorPerformance_Request]#Response}' )
log.info(response)
if(response != "")
{
def jsonResponse = new JsonSlurper().parseText(response)
assert jsonResponse.size() > 0, "Processor API Response is empty"
log.info("Verifying Response")
for (int i = 0; i < jsonResponse.size(); i++) {
	def jsonObj = jsonResponse[i]
	assert jsonObj.containsKey("createTimeUTC"), "createTimeUTC not found in response"
	assert jsonObj.containsKey("createdBy"), "createdBy not found in response"
	assert jsonObj.containsKey("index"), "index not found in response"
	assert jsonObj.containsKey("name"), "name not found in response"
	assert jsonObj.containsKey("type"), "type not found in response"

	assert jsonObj.containsKey("metric"), "metric not found in response"
	assert jsonObj.containsKey("cpus"), "cpus not found in response"
}
}
else
{
	testRunner.fail("Processor API response is empty..ErrPerfService")
}

import groovy.json.JsonSlurper

def response = context.expand( '${#[ProcessorAPI Response Format Validation TestSuite#C816729 - Validate REST endpoint#GetProcessorPerformance_Request]#Response}' )
log.info(response)
if(response != "")
{
def jsonResponse = new JsonSlurper().parseText(response)
assert jsonResponse.size() > 0, "Processor API Response is empty"
assert jsonResponse.size() > 0, "Response is empty"
for (int i = 0; i < jsonResponse.size(); i++) {
	def jsonObj = jsonResponse[i]
	assert jsonObj.containsKey("metric"), "metric not found in response"
	def metricJsonObj = jsonObj.metric
	assert metricJsonObj.containsKey("numOfProcesses"), "numOfProcesses not found in response"
	assert metricJsonObj.containsKey("percentIOTime"), "percentIOTime not found in response"
	assert metricJsonObj.containsKey("percentIdleTime"), "percentIdleTime not found in response"
	assert metricJsonObj.containsKey("percentInterruptTime"), "percentInterruptTime not found in response"
	assert metricJsonObj.containsKey("percentSystemTime"), "percentSystemTime not found in response"
	assert metricJsonObj.containsKey("percentUserTime"), "percentUserTime not found in response"
	assert metricJsonObj.containsKey("percentUtil"), "percentUtil not found in response"
	assert metricJsonObj.containsKey("interruptsPerSec"), "interruptsPerSec not found in response"
}
}
else
{
	testRunner.fail("Processor API response is empty..ErrPerfService")
}

import groovy.json.JsonSlurper

def response = context.expand( '${#[ProcessorAPI Response Format Validation TestSuite#C816729 - Validate REST endpoint#GetProcessorPerformance_Request]#Response}' )
if(response != "")
{
def jsonResponse = new JsonSlurper().parseText(response)
assert jsonResponse.size() > 0, "Processor API Response is empty"
for (int i = 0; i < jsonResponse.size(); i++) {
	def jsonObj = jsonResponse[i]
	assert jsonObj.containsKey("cpus"), "cpus not found in response at: " + i
	def cpusJsonArr = jsonObj.cpus

	assert cpusJsonArr.size() > 0, "cpus array in response is empty"
	for (int j = 0; j < cpusJsonArr.size(); j++) {
		String ij = i + " > " + j
		assert cpusJsonArr[j].containsKey("cores"), "cores not found at: " + ij
		assert cpusJsonArr[j].containsKey("createTimeUTC"), "createTimeUTC not found at: " + ij
		assert cpusJsonArr[j].containsKey("createdBy"), "createdBy not found at: " + ij
		assert cpusJsonArr[j].containsKey("index"), "index not found at: " + ij
		assert cpusJsonArr[j].containsKey("name"), "name not found at: " + ij
		assert cpusJsonArr[j].containsKey("numOfCores"), "numOfCores not found at: " + ij
		assert cpusJsonArr[j].containsKey("metric"), "metric not found at: " + ij
		assert cpusJsonArr[j].containsKey("processorQueueLength"), "processorQueueLength not found at: " + ij
		assert cpusJsonArr[j].containsKey("type"), "type not found at: " + ij
	}
}
}
else
{
	testRunner.fail("Processor API response is empty..ErrPerfService")
}

