import groovy.json.JsonSlurper

def response = context.expand( '${#[ProcessorAPI Response Format Validation TestSuite#C816729 - Validate REST endpoint#GetProcessorPerformance_Request]#Response}' )
if(response != "")
{
def jsonResponse = new JsonSlurper().parseText(response)
assert jsonResponse.size() > 0, "Processor API Response is empty"
for (int i = 0; i < jsonResponse.size(); i++) {
	def jsonObj = jsonResponse[i]

	assert jsonObj.containsKey("cpus"), "cpus not found in response at: " + i
	def cpusJsonArr = jsonObj.cpus

	for (int j = 0; j < cpusJsonArr.size(); j++) {
		String ij = i + " > " + j
		assert cpusJsonArr[j].containsKey("cores"), "cores not found at: " + ij
		def coresJsonArr = cpusJsonArr[j].cores

		assert coresJsonArr.size() > 0, "cores array is empty at: " + ij
		for (int k = 0; k < coresJsonArr.size(); k++) {
			String ijk = ij + " > " + k

			assert coresJsonArr[k].containsKey("createTimeUTC"), "createTimeUTC not at: " + ijk
			assert coresJsonArr[k].containsKey("createdBy"), "createdBy not found at: " + ijk
			assert coresJsonArr[k].containsKey("index"), "index not found at: " + ijk
			assert coresJsonArr[k].containsKey("name"), "name not found at: " + ijk
			assert coresJsonArr[k].containsKey("metric"), "metric not found at: " + ijk
			assert coresJsonArr[k].containsKey("type"), "type not found at: " + ijk
		}
	}
}
}
else
{
	testRunner.fail("Processor API response is empty..ErrPerfService")
}

import groovy.json.JsonSlurper

def response = context.expand( '${#[ProcessorAPI Response Format Validation TestSuite#C816729 - Validate REST endpoint#GetProcessorPerformance_Request]#Response}' )
if(response != "")
{
def jsonResponse = new JsonSlurper().parseText(response)
assert jsonResponse.size() > 0, "Processor API Response is empty"
for (int i = 0; i < jsonResponse.size(); i++) {
	def jsonObj = jsonResponse[i]

	assert jsonObj.containsKey("cpus"), "cpus not found in response"
	def cpusJsonArr = jsonObj.cpus

	for (int j = 0; j < cpusJsonArr.size(); j++) {
			assert cpusJsonArr[j].containsKey("cores"), "cores not found"
			def coresJsonArr = cpusJsonArr[j].cores

			assert coresJsonArr.size() > 0, "cores field is empty"
			for (int k = 0; k < coresJsonArr.size(); k++) {

				assert coresJsonArr[k].containsKey("metric"), "metric not found in response"
				def metricJsonObj = coresJsonArr[k].metric
				assert metricJsonObj.containsKey("numOfProcesses"), "numOfProcesses not found"
				assert metricJsonObj.containsKey("percentIOTime"), "percentIOTime not found"
				assert metricJsonObj.containsKey("percentIdleTime"), "percentIdleTime not found"
				assert metricJsonObj.containsKey("percentInterruptTime"), "percentInterruptTime not found"
				assert metricJsonObj.containsKey("percentSystemTime"), "percentSystemTime not found"
				assert metricJsonObj.containsKey("percentUserTime"), "percentUserTime not found"
				assert metricJsonObj.containsKey("percentUtil"), "percentUtil not found"
				assert metricJsonObj.containsKey("interruptsPerSec"), "interruptsPerSec not found"
				}
		}
	}
}
else
{
	testRunner.fail("Processor API response is empty..ErrPerfService")
}

