import groovy.json.JsonSlurper
import java.text.SimpleDateFormat

def response = context.expand( '${#[ProcessorAPI Response Format Validation TestSuite#C816729 - Validate REST endpoint#GetProcessorPerformance_Request]#Response}' )
if(response != "")
{
def jsonResponse = new JsonSlurper().parseText(response)
assert jsonResponse.size() > 0, "Processor API Response is empty"
for (int i = 0; i < jsonResponse.size(); i++) {
	def createTimeUTC = jsonResponse[i].createTimeUTC
	assert jsonResponse[i].containsKey('createTimeUTC'), "createTimeUTC field not found"
	assert createTimeUTC.trim() != "", "createTimeUTC field is empty"

	def dateTimeFormatStr = "yyyy-MM-dd'T'HH:mm:ss.SSS'Z'"
	SimpleDateFormat sdf = new SimpleDateFormat(dateTimeFormatStr);
	boolean isValidDate = false;
	try {
		sdf.parse(createTimeUTC)
		isValidDate = true;
	} catch (Exception ex) {}
	assert isValidDate, "createTimeUTC is not in format: " + dateTimeFormatStr +"at index "+i
}
}
else
{
	testRunner.fail("Processor API response is empty..ErrPerfService")
}

