			boolean isValidDate = false;
			try {
				sdf.parse(cpusCreateTimeUTC)
				isValidDate = true;
			} catch (Exception ex) {}
			assert isValidDate, "cpus > createTimeUTC is not in format: " + dateTimeFormatStr
		}
	}
