import groovy.json.JsonSlurper

def response = context.expand( '${#[ProcessorAPI Response Format Validation TestSuite#C816729 - Validate REST endpoint#GetProcessorPerformance_Request]#Response}' )
if(response != "")
{
def jsonResponse = new JsonSlurper().parseText(response)
assert jsonResponse.size() > 0, "Processor API Response is empty"
//for (int i = 0; i < jsonResponse.size(); i++) {
for (int i = 0; i < 5; i++) {
	def jsonObj = jsonResponse[i]
	assert jsonObj.containsKey("cpus"), "cpus not found in response"
	def cpusJsonArr = jsonObj.cpus

	assert cpusJsonArr.size() > 0, "cpus array in response is empty at: "+i
	for (int j = 0; j < cpusJsonArr.size(); j++) {
		String ij = i + " > " + j
			assert cpusJsonArr[j].containsKey("numOfCores"), "cpus > numOfCores not found at: " +ij
			String cpusNumOfCoresStr = cpusJsonArr[j].numOfCores
			assert cpusNumOfCoresStr.isInteger(), "cpus > numOfCores is not an integer value at: " +ij
			int cpusNumOfCores = cpusNumOfCoresStr as int
			assert cpusNumOfCores > 0, "cpus > numOfCores is not greater than 0 at: " +ij

			assert cpusJsonArr[j].containsKey("cores"), "cpus > cores not found at: " +ij
			int cpusCoresArrSize = cpusJsonArr[j].cores.size()
			assert cpusCoresArrSize == cpusNumOfCores, "cpus > numOfCores is not equal to size of cpus > cores array at: " +ij
		}
	}
}
else
{
	testRunner.fail("Processor API response is empty..ErrPerfService")
}

