{
def jsonResponse = new JsonSlurper().parseText(response)
assert jsonResponse.size() > 0, "Processor API Response is empty"
for (int i = 0; i < jsonResponse.size(); i++) {
	def jsonObj = jsonResponse[i]
	assert jsonObj.containsKey("cpus"), "cpus not found in response"
	def cpusJsonArr = jsonObj.cpus

	assert cpusJsonArr.size() > 0, "cpus array in response is empty"
	for (int j = 0; j < cpusJsonArr.size(); j++) {
			assert cpusJsonArr[j].containsKey("metric"), "cpus > metric not found in JSON response"
			assert cpusJsonArr[j].metric != null, "cpus > metric property is null"
			assert cpusJsonArr[j].metric.containsKey("numOfProcesses"), "cpus > metric > numOfProcesses property not found"
			assert cpusJsonArr[j].metric.numOfProcesses != null, "cpus > metric > numOfProcesses property is null"

			String numOfProcessStr = cpusJsonArr[j].metric.numOfProcesses
			assert numOfProcessStr.isInteger(), "cpus > metric > numOfProcesses is not an integer value"
			int numOfProcess = numOfProcessStr as int
			assert numOfProcess >= 0, "cpus > metric > numOfProcesses is negative"
		}
	}
}
else
{
	testRunner.fail("Processor API response is empty..ErrPerfService")
}

import groovy.json.JsonSlurper

def response = context.expand( '${#[ProcessorAPI Response Format Validation TestSuite#C816729 - Validate REST endpoint#GetProcessorPerformance_Request]#Response}' )
if(response != "")
{
def jsonResponse = new JsonSlurper().parseText(response)
assert jsonResponse.size() > 0, "Processor API Response is empty"
for (int i = 0; i < jsonResponse.size(); i++) {
	def jsonObj = jsonResponse[i]
	assert jsonObj.containsKey("cpus"), "cpus not found in response"
	def cpusJsonArr = jsonObj.cpus

	assert cpusJsonArr.size() > 0, "cpus array in response is empty"
	for (int j = 0; j < cpusJsonArr.size(); j++) {
			assert cpusJsonArr[j].containsKey("metric"), "cpus > metric not found in JSON response"
			assert cpusJsonArr[j].metric != null, "cpus > metric property is null"
			assert cpusJsonArr[j].metric.containsKey("percentIOTime"), "cpus > metric > percentIOTime property not found"
			assert cpusJsonArr[j].metric.percentIOTime != null, "cpus > metric > percentIOTime property is null"

			String percentStr = cpusJsonArr[j].metric.percentIOTime
			assert percentStr.isFloat(), "cpus > metric > percentIOTime is not a numeric value"
			float percent = percentStr as float
			assert percent >= 0 && percent <= 100, "cpus > metric > percentIOTime is not a valid percent value"
		}
	}
}
else
{
	testRunner.fail("Processor API response is empty..ErrPerfService")
}

