	assert jsonObj.containsKey("cpus"), "cpus not found in response"
	def cpusJsonArr = jsonObj.cpus

	assert cpusJsonArr.size() > 0, "cpus array in response is empty"
	for (int j = 0; j < cpusJsonArr.size(); j++) {
			assert cpusJsonArr[j].containsKey("cores"), "cpus > cores not found in JSON response"
			assert cpusJsonArr[j].cores != null, "cpus > cores is null"
			def coresJsonArr = cpusJsonArr[j].cores

			assert coresJsonArr.size() > 0, "cpus > cores property is empty"
			for (int k = 0; k < coresJsonArr.size(); k++) {
				assert coresJsonArr[k].containsKey("metric"), "cpus > cores > metric not found in JSON response"
				assert coresJsonArr[k].metric != null, "cpus > cores > metric property is null"
				assert coresJsonArr[k].metric.containsKey("percentUtil"), "cpus > cores > metric > percentUtil property not found"
				assert coresJsonArr[k].metric.percentUtil != null, "cpus > cores > metric > percentUtil property is null"

				String percentStr = coresJsonArr[k].metric.percentUtil
				assert percentStr.isFloat(), "cpus > cores > metric > percentUtil is not a numeric value"
				float percent = percentStr as float
				assert percent >= 0 && percent <= 100, "cpus > cores > metric > percentUtil is not a valid percent value"
			}
		}
	}
}
else
{
	testRunner.fail("Processor API response is empty..ErrPerfService")
}

