import groovy.json.JsonSlurper
import java.text.SimpleDateFormat

def response = context.expand( '${#[StorageAPI Response Format Validation TestSuite#C907728 - Validate REST endpoint#GetStoragePerformance_Request]#Response}' )
if(response != "")
{
def jsonResponse = new JsonSlurper().parseText(response)
assert jsonResponse.size() > 0, "Storage API Response is empty"
String jsonPath = "storages > index"

for (int i = 0; i < jsonResponse.size(); i++) {
	def jsonObj = jsonResponse[i]
	assert jsonObj.containsKey("storages"), "storages not found in response array at index: " + i
	def storagesJsonArr = jsonObj.storages
	assert storagesJsonArr != null, "storages property is null at: " + i

	assert storagesJsonArr.size() > 0, "storages array in response is empty at index: " + i
	for (int j = 0; j < storagesJsonArr.size(); j++) {
		String position = i + " > " + j
		assert storagesJsonArr[j].containsKey("index"), jsonPath + " not found in JSON response at: " + position
		String storagesIndexStr = storagesJsonArr[j].index
		assert storagesIndexStr != null, jsonPath + " is null at: " + position

		assert storagesIndexStr.isInteger(), jsonPath + " is not an integer value at: " + position
		int storagesIndex = storagesIndexStr as int
		assert storagesIndex == j, jsonPath + " is incorrect at: " + position
	}
}
}
else
{
	testRunner.fail("Storage API response is empty..ErrPerfService")
}

