import groovy.json.JsonSlurper
import java.text.SimpleDateFormat

def response = context.expand( '${#[StorageAPI Response Format Validation TestSuite#C907728 - Validate REST endpoint#GetStoragePerformance_Request]#Response}' )
if(response != "")
{
def jsonResponse = new JsonSlurper().parseText(response)
assert jsonResponse.size() > 0, "Storage API Response is empty"
String parentJsonPath = "storages > partitions"
String jsonPath = parentJsonPath + " > " + "createTimeUTC"

for (int i = 0; i < jsonResponse.size(); i++) {
	def jsonObj = jsonResponse[i]
	assert jsonObj.containsKey("storages"), "storages not found in response array at index: " + i
	def storagesJsonArr = jsonObj.storages

	assert storagesJsonArr.size() > 0, "storages array in response is empty at index: " + i
	for (int j = 0; j < storagesJsonArr.size(); j++) {
		assert storagesJsonArr[j].containsKey("partitions"), parentJsonPath + " property not found at: " + i + " > " + j
		def partitionsJsonArr = storagesJsonArr[j].partitions

		assert partitionsJsonArr.size() > 0, parentJsonPath + " array is empty at: " + i + " > " + j
		for (int k = 0; k < partitionsJsonArr.size(); k++) {
			String position = i + " > " + j + " > " + k;
			assert partitionsJsonArr[k].containsKey("createTimeUTC"), jsonPath + " not found at: " + position
			def createTimeUTC = partitionsJsonArr[k].createTimeUTC
			assert createTimeUTC != null, jsonPath + " field is null at: " + position
			assert createTimeUTC.trim() != "", jsonPath + " field is empty at: " + position

			def dateTimeFormatStr = "yyyy-MM-dd'T'HH:mm:ss.SSS'Z'"
			SimpleDateFormat sdf = new SimpleDateFormat(dateTimeFormatStr);
			boolean isValidDate = false;
			try {
				sdf.parse(createTimeUTC)
				isValidDate = true;
			} catch (Exception ex) {}
			assert isValidDate, jsonPath + " is not in proper format at: " + position
		}
		}
	}
}
else
{
	testRunner.fail("Storage API response is empty..ErrPerfService")
}

